import React from 'react';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';


const Header = () => {
const location = useLocation();
const { pathname } = location;
const splitLocation = pathname.split("/");
console.log(splitLocation);
return (
        <header id="header" className="fixed-top d-flex align-items-center">
          <div className="container d-flex align-items-center">
            <h1 className="logo me-auto"><a href="index.html">Presento<span>.</span></a></h1>
            <a href="index.html" className="logo me-auto">
                <img src="assets/img/logo.png" alt=""/></a>
            <nav id="navbar" className="navbar order-last order-lg-0">
              <ul>
              <li>
                  <Link className={`nav-link scrollto ${splitLocation[1] === "" ? "active":""}`} to={'/'}>Home</Link>
                  </li>
                <li>
                  <Link className={`nav-link scrollto ${splitLocation[1] === "about"? "active":""}`} to={'/about'}>About Us</Link>
                </li>
                <li>
                  <Link className={`nav-link scrollto ${splitLocation[1] === "blog"? "active":""}`} to={'/blog'}>Blog</Link>
                </li>
                <li>
                  <Link className={`nav-link scrollto ${splitLocation[1] === "contact"? "active":""}`} to={'/contact'}>Contact</Link>
                </li>
              </ul>
              <i className="bi bi-list mobile-nav-toggle"></i>
            </nav>      
            <a href="/about" className="get-started-btn scrollto">Get Started</a>
          </div>
        </header>
    );
}

export default Header;
